import React, { useState } from 'react';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from '../ExpenseFilter/ExpenseFilter';
import './Expenses.css';

const Expenses = (props) => {
	const [filteredYear, setFilteredYear] = useState('2020');

	const filterChangeHandler = (selectedYear) => {
		setFilteredYear(selectedYear);
	};
	const filteredExpenses = props.items.filter(expee => {
		return expee.date.getFullYear().toString() === filteredYear;
	});
	let expneseContent = <p>No expense Found</p>;

	if (filteredExpenses.length > 0) {
		expneseContent = filteredExpenses.length > 0 &&
			filteredExpenses.map((expee) => (
				<ExpenseItem
					key={expee.id}
					title={expee.title}
					amount={expee.amount}
					date={expee.date}
				/>
			))
	}

	return (
		<div>
			<Card className='expenses'>
				<ExpensesFilter
					selected={filteredYear}
					onChangeFilter={filterChangeHandler}
				/>
				{
					expneseContent
				}
				
			</Card>
		</div>
	);
};

export default Expenses;
